mapper.insert:
INSERT INTO brd_bm (
		brd_uni_no,
        partici_memb_uni_no
        )
VALUES (#{brdUniNo},
		#{particiMembUniNo}
		)

mapper.select:
SELECT count(*)
FROM brd_bm
WHERE brd_uni_no = #{brdUniNo} and partici_memb_uni_no=#{particiMembUniNo}

SELECT COUNT(*) 
FROM bm
WHERE post_uni_no = #{postUniNo} and partici_memb_uni_no = #{particiMembUniNo}

mapper.delete:
DELETE bm 
WHERE

bm_uni_no = #{bmUniNo}


partici_memb_uni_no = #{particiMembUniNo} AND att_no = #{attNo}



partici_memb_uni_no = #{particiMembUniNo} AND post_uni_no = #{postUniNo}



partici_memb_uni_no = #{particiMembUniNo} AND chat_no = #{chatNo}
