mapper.update:
 		UPDATE prj
		   SET prj_nm = #{prjNm}
		 WHERE prj_uni_no = #{prjUniNo}

 		UPDATE prj
		SET subsp_yn = #{subspYn}
		WHERE prj_uni_no = #{prjUniNo}

mapper.select:
SELECT *
  FROM prj_partici_memb
  WHERE prj_uni_no = #{prjUniNo}
	AND memb_uni_no = #{membUniNo}

mapper.update:
  		UPDATE prj_partici_memb
		SET grd = #{grd}
		WHERE partici_memb_uni_no = #{particiMembUniNo}

mapper.select:
SELECT PARTICI_MEMB_UNI_NO, PRJ_UNI_NO, MEMB_UNI_NO, ATT_NO, NNM, GRD, FIND_CODE_NAME(GRD) AS CD, PARTICI_YN, PRJ_PUBC_ID , DEPT , POS from prj_partici_memb pm left join cmmn c on c.cd_no=pm.grd WHERE pm.partici_yn = 'Y'
AND pm.memb_uni_no = #{membUniNo}
AND c.cd = #{cd}
and pm.prj_uni_no = #{prjUniNo}

SELECT count(*)
FROM   brd
WHERE  prj_uni_no=#{prjUniNo}

SELECT count(*)
FROM   chatr
WHERE  prj_uni_no = #{prjUniNo}
